'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var join = require( 'path' ).join;
var spawn = require( 'child_process' ).spawnSync; // eslint-disable-line node/no-sync
var logger = require( 'debug' );
var parse = require( 'yaml' ).parse;
var substringAfter = require( '@stdlib/string/substring-after' );
var replace = require( '@stdlib/string/replace' );
var trim = require( '@stdlib/string/trim' );
var filterFiles = require( './filter_files.js' );


// VARIABLES //

var debug = logger( 'changelog:parse-commits' );
var GIT_COMMIT_SEP = '^---^';
var YAML_SEP = '---';


// FUNCTIONS //

/**
* Escapes double quotes in a string.
*
* @private
* @param {string} str - input string
* @returns {string} escaped string
*
* @example
* var str = escapeDoubleQuotes( 'beep "boop"' );
* // returns 'beep \"boop\"'
*/
function escapeDoubleQuotes( str ) {
	return replace( str, '"', '\\"' );
}

/**
* Extracts a YAML block surrounded by delimiters from a string.
*
* @private
* @param {string} str - input string
* @returns {string} extracted YAML block
*/
function extractYAMLBlock( str ) {
	var start = str.indexOf( YAML_SEP ) + YAML_SEP.length;
	var end = str.indexOf( YAML_SEP, start );
	return str.substring( start, end );
}


// MAIN //

/**
* Extracts commit details for a specified directory.
*
* @private
* @param {string} dir - directory for which to extract commit details
* @returns {Array} array of commit details
*
* @example
* var commits = extractCommits( '.' );
* // returns [...]
*/
function extractCommits( dir ) {
	var commits;
	var author;
	var parsed;
	var files;
	var lines;
	var notes;
	var parts;
	var args;
	var hash;
	var date;
	var opts;
	var yaml;
	var cmd;
	var msg;
	var out;
	var i;

	opts = {
		'maxBuffer': 1024*1024*60, // 60 MB
		'stdio': [ 'pipe', 'pipe', 'ignore' ], // stdin, stdout, stderr
		'env': {
			'GIT_COMMIT_SEP': GIT_COMMIT_SEP
		}
	};
	cmd = join( __dirname, '..', 'scripts', 'commits.sh' );
	commits = [];
	args = [ resolve( dir ) ];
	try {
		out = spawn( cmd, args, opts );
	} catch ( err ) {
		debug( 'Encountered an error when attempting to extract commits: %s', err.message );
		return commits;
	}
	out = trim( out.stdout.toString() );
	lines = out.split( GIT_COMMIT_SEP );
	for ( i = 0; i < lines.length; i++ ) {
		if ( !lines[ i ] ) {
			continue;
		}
		parts = lines[ i ].split( '|' );
		hash = trim( parts[ 0 ] );
		date = parts[ 1 ];
		author = parts[ 2 ];

		msg = trim( escapeDoubleQuotes( parts[ 3 ] ) );
		notes = parts[ 4 ];
		files = trim( parts[ 5 ] ).split( '\n' );
		if ( notes ) {
			// Extract YAML block from git notes and apply any specified transformations:
			yaml = extractYAMLBlock( notes );
			parsed = parse( yaml );
			switch ( parsed.type ) {
			case 'amend-message':
				// Replace the commit message with the message from the notes:
				msg = substringAfter( notes, yaml + YAML_SEP + '\n' );
				break;
			case 'filter-packages':
				// Filter out files which are part of the excluded packages:
				files = filterFiles( files, parsed.exclude );
				break;
			default:
				break;
			}
		}
		commits.push({
			'hash': hash,
			'date': date,
			'author': author,
			'message': msg,
			'files': files
		});
	}
	return commits;
}


// EXPORTS //

module.exports = extractCommits;
