'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var stdlib = require(\'@stdlib/math/base/special/betainc\');'
	].join( '\n' ),
	'errors': [
		{
			'message': 'There should be exactly one space after the opening parenthesis in require calls.',
			'type': 'CallExpression'
		},
		{
			'message': 'There should be exactly one space before the closing parenthesis in require calls.',
			'type': 'CallExpression'
		}
	],
	'output': [
		'// MODULES //',
		'',
		'var stdlib = require( \'@stdlib/math/base/special/betainc\' );'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var math = require( \'@stdlib/math\');'
	].join( '\n' ),
	'errors': [
		{
			'message': 'There should be exactly one space before the closing parenthesis in require calls.',
			'type': 'CallExpression'
		}
	],
	'output': [
		'// MODULES //',
		'',
		'var math = require( \'@stdlib/math\' );'
	].join( '\n' )
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var validate = require(\'./validate.js\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'There should be exactly one space after the opening parenthesis in require calls.',
			'type': 'CallExpression'
		}
	],
	'output': [
		'// MODULES //',
		'',
		'var validate = require( \'./validate.js\' );'
	].join( '\n' )
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
